import React from "react";
import Cover from "../assets/images/Cover.png";



export default function Header() {
  const bgImageHeader = {
    backgroundImage: `url(${Cover})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    width: "100%",
    height: "100%",
    position: "absolute",
  };

  const overlay = {
    content: "",
    position: "absolute",
    top: "0",
    left: "0",
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, .45)" /* Black with 24% opacity */,
  };

  return (
    <div >
      <div className="bg-image" style={bgImageHeader}>
        <div className="overlay " style={overlay}>
          <div>
            <h1
              style={{
                fontFamily: "serif",
                fontSize: "5rem",
                marginTop: "2.5rem",
                color: "white",
              }}
            >
              301_CATCHING
            </h1>
            <h3>
                The
                <span className="red-text"> "FOUNDATIONAL EXPERTS"</span> for
                everything catching: 
                <br></br>
                Providing Players with the skills to master their craft & elevate their game.
              </h3>
              </div>
              </div>
     
          </div>

          <div
            style={{
              color: "white",
            }}
          >
            <h4
              style={{
                fontFamily: "bellota text",
                textDecoration: "bold",
                fontSize: "2rem",
                textAlign: "center",
                color: "white",
              }}
            >
              UPCOMING CAMPS & EVENTS{" "}
            </h4>

            
        </div>
        </div>
   
 
  );
}
